
import { DietChart, WeeklyPlan, Hba1cLevel, DietType, ExerciseType, Language, Trimester, DailyMeal, PediatricAgeGroup, InfantSubGroup } from "../types";

// Helper to create a diet chart object
const createChart = (title: string, macros: string, weekly: WeeklyPlan, summary?: string, guidelines?: string[]): DietChart => ({
  title,
  macros,
  weekly,
  summary,
  guidelines
});

