export const getGdmDietPlan = (hba1c: Hba1cLevel, trimester: Trimester, diet: DietType, exercise: ExerciseType, lang: Language): DietChart => {
  if (hba1c === ">10") {
      const trimesterKey = trimester === '1' ? '1' : trimester === '2' ? '2' : trimester === '3' ? '3' : '1_OVERT';
      return GDM_CHART_4[diet][exercise][trimesterKey];
  }
  const basePlan = DIABETIC_PLANS[hba1c]?.[diet]?.[exercise] || DIABETIC_PLANS["6.5-7"]["Veg"]["None"];
  let calorieAddon = "";
  if (trimester === "2") calorieAddon = " (+340 kcal for T2)";
  if (trimester === "3") calorieAddon = " (+450 kcal for T3)";
  
  return {
      ...basePlan,
      title: `GDM Diet (${hba1c}%) - T${trimester} ${calorieAddon}`,
      macros: basePlan.macros + (trimester !== "1" ? " (Increased)" : ""),
      guidelines: [
          "Monitor blood sugar after 1 hour of meal.",
          "Walking after meals is highly recommended.",
          ...(basePlan.guidelines || [])
      ]
  };
};

