export const getPediatricDietPlan = (ageGroup: PediatricAgeGroup, subGroup: InfantSubGroup | null, lang: Language): DietChart => {
  if (ageGroup === '6-12m') {
      if (subGroup === '6-8m') return PEDIATRIC_PLANS["6-8m"];
      if (subGroup === '9-12m') return PEDIATRIC_PLANS["9-12m"];
      return PEDIATRIC_PLANS["6-8m"];
  }
  return PEDIATRIC_PLANS[ageGroup] || PEDIATRIC_PLANS["1-3y"];
};
